import { ThemeOptions } from '@mui/material/styles';

export const themeOptions = (props) => {
  const mode = (
    props.light === 'light'
  )
  ? 'light'
  : 'dark';
  return ({
    palette: {
      mode: 'light',
      primary: {
        main: '#7c4dff',
      },
      secondary: {
        main: '#f50057',
      },
    },
  })
};
