import { ThemeOptions } from '@mui/material/styles';
import { useState } from 'react';

export const themeOptions = (props) => {
  const [mode, setMode] = useState(null);

  setMode(( props.light === 'light') ? 'light' : 'dark');

  return ({
    palette: {
      mode: mode,
      primary: {
        main: '#7c4dff',
      },
      secondary: {
        main: '#f50057',
      },
    },
  })
};
